@Component({
  selector: 'app-contact',
  templateUrl: './contact.component.html',
  styles: [
  ]
})
export class ContactComponent {

  constructor() { }

}
<article class="post page post-full">
  <header class="post-header">
    <h1 class="post-title underline">Contact</h1>
  </header><!-- .post-header -->

  <div class="post-content">
    <p>Call me on <span style="font-weight: bold;">+27 (0)60 840 7793</span></p>

    <p>Email me on <span style="font-weight: bold;">allen @ codestream.co.za</span></p>


  </div><!-- .post-content -->
</article><!-- .post -->
